import {createStore} from "vuex";
import getters from "./getters";
import mutations from "./mutations";
import actions from "./actions";    //추가

export default createStore({
    state: {
        user: null,
        isLogin: false,
        info: {
            id: '',
            name: ''
        }
    },
    mutations: {
        SET_USER(state, user) {
            state.user = user
        }
    },
    actions: {
        login({commit}, user) {
            // 로그인 처리 로직
            commit('SET_USER', user)
        },
        logout({commit}) {
            // 로그아웃 처리 로직
            commit('SET_USER', {id: '', name: ''})
        }
    },
    getters//추가
});
