import * as types from './mutation_types'

export default {
    [types.USER_ID] (state, userId) {
        state.userId = userId
    },
    [types.ERROR_STATE] (state, errorState) {
        state.errorState = errorState
    },
    [types.IS_AUTH] (state, isAuth) {
        state.isAuth = isAuth
    },
    SET_USER(state, user) {
        state.user = user;
        state.isLogin = true;
        state.username = user.name;
    },
    CLEAR_USER(state) {
        state.user = null;
        state.isLogin = false;
        state.username = null;
    },
}
